package loop;
public class Loop {
	//it is used to iterate part of program multiple times.
	//syntax :- for(initialization; condition check; incr/decr){
    // executable code}
//1.initialization	
	//2.condition check
	//3. executable code
	//4. incr/decr
	//loop will execute till the condition get false.
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
    for(int i=0; i<=5; i++) {
    	System.out.println(i);
    }
    System.out.println("Control came out from loop");
	}

}
